#!/bin/sh
# build shared library
export STIGMA_PATH=`pwd`;
cd "/tmp"
if [ `uname` = "Darwin" ]; then
 clang++ -c "$STIGMA_PATH/Widget_Systems/SDL/ImFileDialog.cpp" "$STIGMA_PATH/Widget_Systems/SDL/imgui.cpp" "$STIGMA_PATH/Widget_Systems/SDL/imgui_impl_sdl.cpp" "$STIGMA_PATH/Widget_Systems/SDL/imgui_impl_sdlrenderer.cpp" "$STIGMA_PATH/Widget_Systems/SDL/imgui_draw.cpp" "$STIGMA_PATH/Widget_Systems/SDL/imgui_tables.cpp" "$STIGMA_PATH/Widget_Systems/SDL/imgui_widgets.cpp" "$STIGMA_PATH/Widget_Systems/SDL/msgbox/imguial_msgbox.cpp" "$STIGMA_PATH/../../shared/apifilesystem/filesystem.cpp" "$STIGMA_PATH/Widget_Systems/SDL/filedialogs.cpp" -std=c++17 -DIFD_SHARED_LIBRARY -shared -I"$STIGMA_PATH/../../shared/apifilesystem" -I"$STIGMA_PATH/../../shared" -Wno-format-security -I"$STIGMA_PATH" -DIMGUI_USE_WCHAR32 -I/opt/local/include -I/opt/local/include/SDL2 -ObjC++ -fPIC -arch arm64 -arch x86_64 -fPIC
 ar rc "/tmp/stigma.a" "/tmp/ImFileDialog.o" "/tmp/imgui.o" "/tmp/imgui_impl_sdl.o" "/tmp/imgui_impl_sdlrenderer.o" "/tmp/imgui_draw.o" "/tmp/imgui_tables.o" "/tmp/imgui_widgets.o" "/tmp/imguial_msgbox.o" "/tmp/filesystem.o" "/tmp/filedialogs.o"
