 ar rc "/tmp/stigma.a"  "/tmp/ImFileDialog.o" "/tmp/imgui.o" "/tmp/imgui_impl_sdl.o" "/tmp/imgui_impl_sdlrenderer.o" "/tmp/imgui_draw.o" "/tmp/imgui_tables.o" "/tmp/imgui_widgets.o" "/tmp/imguial_msgbox.o" "/tmp/filesystem.o" "/tmp/filedialogs.o"
